#include <iostream>

using namespace std;

class Solution {
public:
    bool linear(string s, string t) {
      if (s.length() > t.length())
        return false;
        
      int i = 0, j = 0;
      while (i < s.length() && j < t.length()) {
        if (s[i] == t[j++]) {
          i++;
        }
      }

      return i == s.length();
    }

    bool isSubsequence(string s, string t) {
      return this->linear(s, t);
    }
};

int main() {
  Solution s;
  string a = "abc", b = "ahbgdc";
  cout << s.isSubsequence(a, b) << endl;
  cout << s.isSubsequence("axc", "ahbgdc") << endl;
  cout << s.isSubsequence("", "ahbgdc") << endl;
  cout << s.isSubsequence("bcd",
"") << endl;
  return 0;
}

